import React, { useState } from "react";
import bgImage from "./bg.png"; // Background Image
import userProfile from "./profile.png"; // Profile Picture

const feedbacks = [
    {
        id: 1,
        name: "Floyd Miles",
        feedback:
            "Amet minim mollit non deserunt ullamco est sit aliqua dolor do amet sint. Velit officia consequat duis enim velit mollit.",
        rating: 4,
        image: "/Rating.png",
    },
    {
        id: 2,
        name: "Ronald Richards",
        feedback:
            "ullamco est sit aliqua dolor do amet sint. Velit officia consequat duis enim velit mollit.",
        rating: 5,
        image: "/Rating.png",
    },
    {
        id: 3,
        name: "Savannah Nguyen",
        feedback:
            "Amet minim mollit non deserunt ullamco est sit aliqua dolor do amet sint.",
        rating: 4,
        image: "/Rating.png",
    },
];

const Profile = () => {
    const [isEditing, setIsEditing] = useState(false);

    // User Profile State
    const [profile, setProfile] = useState({
        bio: "",
        fullName: "Aathif Mohamed",
        university: "University of Ruhuna",
        gender: "Male",
        mobileNo: "0779981298",
        emergencyMobileNo: "0779981298",
        address: "No 53/B Akurana, Kandy",
        password: "********",
        confirmPassword: "********",
    });

    // Handle input change
    const handleChange = (e) => {
        setProfile({
            ...profile,
            [e.target.name]: e.target.value,
        });
    };

    // Toggle edit mode
    const handleEditToggle = () => {
        setIsEditing(!isEditing);
    };

    return (
        <div className="bg-gray-100 min-h-screen relative">
            {/* Hero Section */}
            <header className="relative flex flex-col justify-center items-center text-white text-align h-[50vh] bg-cover bg-center"
                    style={{ backgroundImage: `url(${bgImage})` }}>
                <div className="relative z-10 text-center">
                    <h1 className="text-4xl md:text-6xl font-bold text-black">
                        Welcome, <span className="text-blue-400">{profile.fullName}</span>
                    </h1>
                </div>
            </header>

            {/* Profile Card */}
            <div className="max-w-5xl mx-auto bg-white rounded-lg shadow-md -mt-20 p-6 relative z-10">
                <div className="flex items-center relative">
                    {/* Profile Image (Brought to Front) */}
                    <div className="relative z-20">
                        <img src={userProfile} alt="Profile"
                             className="w-32 h-32 rounded-full border-4 border-white shadow-lg" />
                    </div>

                    {/* User Details */}
                    <div className="ml-6">
                        <h2 className="text-xl font-bold">{profile.fullName}</h2>
                        <p className="text-gray-500">{profile.mobileNo}</p>
                    </div>

                    {/* Edit / Save Button (Brought to Front) */}
                    <button
                        onClick={handleEditToggle}
                        className={`ml-auto px-6 py-2 rounded-lg font-medium transition z-20 ${
                            isEditing ? "bg-green-500 hover:bg-green-600" : "bg-blue-500 hover:bg-blue-600"
                        } text-white`}>
                        {isEditing ? "Save" : "Edit"}
                    </button>
                </div>

                {/* Editable Form */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mt-6 relative z-10">
                    <textarea
                        name="bio"
                        className="border p-3 rounded w-full h-24"
                        placeholder="Bio"
                        value={profile.bio}
                        onChange={handleChange}
                        disabled={!isEditing}
                    ></textarea>

                    <input
                        type="text"
                        name="fullName"
                        className="border p-3 rounded w-full"
                        placeholder="Full Name"
                        value={profile.fullName}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="text"
                        name="university"
                        className="border p-3 rounded w-full"
                        placeholder="University"
                        value={profile.university}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="text"
                        name="gender"
                        className="border p-3 rounded w-full"
                        placeholder="Gender"
                        value={profile.gender}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="text"
                        name="mobileNo"
                        className="border p-3 rounded w-full"
                        placeholder="Mobile No"
                        value={profile.mobileNo}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="text"
                        name="emergencyMobileNo"
                        className="border p-3 rounded w-full"
                        placeholder="Emergency Mobile No"
                        value={profile.emergencyMobileNo}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="text"
                        name="address"
                        className="border p-3 rounded w-full"
                        placeholder="Address"
                        value={profile.address}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="password"
                        name="password"
                        className="border p-3 rounded w-full"
                        placeholder="Password"
                        value={profile.password}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />

                    <input
                        type="password"
                        name="confirmPassword"
                        className="border p-3 rounded w-full"
                        placeholder="Confirm Password"
                        value={profile.confirmPassword}
                        onChange={handleChange}
                        disabled={!isEditing}
                    />
                </div>
            </div>

            {/* Company Feedback Section */}
            <section className="max-w-6xl mx-auto p-6 relative z-10">
                <h2 className="text-2xl font-bold mb-4">Company Feedback</h2>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                    {feedbacks.map((feedback) => (
                        <div key={feedback.id} className="bg-white shadow-md rounded-lg p-4 flex flex-col">
                            <div className="flex items-center">
                                <img src={feedback.image} alt={feedback.name} className="w-12 h-12 rounded-full" />
                                <div className="ml-3">
                                    <h3 className="text-md font-semibold">{feedback.name}</h3>
                                    <div className="text-yellow-400">
                                        {"⭐".repeat(feedback.rating)}
                                    </div>
                                </div>
                            </div>
                            <p className="text-gray-600 text-sm mt-2">{feedback.feedback}</p>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default Profile;
