import React from "react";
import bgImage from "./bg.png";
import "./Home.css";

const jobs = [
    {
        id: 1,
        company: "K&D Garment",
        description:
            "නොවැම්බර් 16 සිට දෙසැම්බර් 7තෙක් vote activation sampling and selling වැඩසටහන සඳහා sales ප්‍රවර්ධන ක්‍රියාවලිය 2ක් පැවැත්වේ. (9.00am to 6.00pm)",
        rating: 4.5,
        reviews: "5,291 reviews",
        salary: "Rs.3500.00",
        logo: "./1728.png", // Replace with actual logo URL
    },
    {
        id: 2,
        company: "K&D Garment",
        description:
            "නොවැම්බර් 16 සිට දෙසැම්බර් 7තෙක් vote activation sampling and selling වැඩසටහන සඳහා sales ප්‍රවර්ධන ක්‍රියාවලිය 2ක් පැවැත්වේ. (9.00am to 6.00pm)",
        rating: 4.5,
        reviews: "5,291 reviews",
        salary: "Rs.3500.00",
        logo: "https://via.placeholder.com/80",
    },
    {
        id: 3,
        company: "K&D Garment",
        description:
            "නොවැම්බර් 16 සිට දෙසැම්බර් 7තෙක් vote activation sampling and selling වැඩසටහන සඳහා sales ප්‍රවර්ධන ක්‍රියාවලිය 2ක් පැවැත්වේ. (9.00am to 6.00pm)",
        rating: 4.5,
        reviews: "5,291 reviews",
        salary: "Rs.3500.00",
        logo: "https://via.placeholder.com/80",
    },
];

const JobListings = () => {
    return (
        <div className="bg-gray-100 min-h-screen">
            {/* Navbar */}
            <nav className="bg-black text-white p-4 flex justify-between items-center">
                <div className="text-2xl font-bold">Uni Earn</div>
                <div className="flex space-x-6">
                    <a href="#" className="hover:underline">Home</a>
                    <a href="#" className="hover:underline">Company</a>
                    <a href="#" className="hover:underline">Profile</a>
                    <a href="#" className="hover:underline">Activities</a>
                    <a href="#" className="hover:underline">Contact</a>
                    <a href="#" className="hover:underline">Logout</a>
                </div>
            </nav>

            {/* Hero Section */}
            {/*<header*/}
            {/*    className="relative flex flex-col justify-center items-center text-white text-align"*/}
            {/*    style={{*/}
            {/*        backgroundImage: `url(${bgImage})`,*/}
            {/*        backgroundSize: "cover",*/}
            {/*        backgroundPosition: "center",*/}
            {/*        width: "auto",*/}
            {/*        height: "600px",*/}
            {/*        flexShrink: "0",*/}
            {/*    }}*/}
            {/*>*/}


            {/*<h1 className="text-2xl md:text-7xl font-bold text-balance">*/}
            {/*        Find Your Perfect <br />*/}
            {/*        <span className="text-blue-500">Part-Time Job</span>*/}
            {/*    </h1>*/}

            {/*    /!* Search Box *!/*/}
            {/*    <div className="flex bg-white rounded-full p-2 mt-6 w-full max-w-md">*/}
            {/*        <input*/}
            {/*            type="text"*/}
            {/*            placeholder="Job, Category, Keyword, Company"*/}
            {/*            className="flex-grow p-2 outline-none text-black"*/}
            {/*        />*/}
            {/*        <button className="search-container text-white px-6 py-2 rounded-full">*/}
            {/*            Search*/}
            {/*        </button>*/}
            {/*    </div>*/}
            {/*</header>*/}
            <div className="relative h-[50vh] bg-cover bg-center flex flex-col items-center justify-center text-center text-white" style={{ backgroundImage: "url('/background.jpg')" }}>
                <div className="absolute inset-0 bg-black bg-opacity-50"></div>
                <div className="relative z-10">
                    <h1 className="text-4xl md:text-6xl font-bold">
                        Find Your Perfect <span className="text-blue-400">Part-Time</span> Job
                    </h1>
                    <div className="mt-6 flex justify-center">
                        <input type="text" placeholder="Job, Category, Keyword ,Company" className="w-[300px] md:w-[500px] p-3 rounded-l-lg focus:outline-none text-gray-700" />
                        <button className="bg-blue-500 px-6 py-3 rounded-r-lg text-white font-semibold">Search</button>
                    </div>
                </div>
            </div>
            {/* Job Listings */}
            <section className="max-w-4xl mx-auto p-6">
                <h2 className="text-2xl font-bold mb-4">Explore All Jobs</h2>

                {jobs.map((job) => (
                    <div key={job.id} className="bg-white p-4 rounded-xl shadow-md flex items-center mb-4">
                        {/* Job Logo */}
                        <div className="w-16 h-16 flex-shrink-0 mr-4">
                            <img src={job.logo} alt="Company Logo" className="rounded-full w-full h-full" />
                        </div>

                        {/* Job Details */}
                        <div className="flex-grow">
                            <h3 className="text-lg font-bold">{job.company}</h3>
                            <p className="text-gray-600 text-sm">{job.description}</p>

                            {/* Rating and Salary */}
                            <div className="flex items-center mt-2 text-yellow-400">
                                {"⭐".repeat(Math.floor(job.rating))} <span className="text-gray-500 text-sm ml-2">{job.reviews}</span>
                            </div>

                            <p className="text-green-600 font-bold mt-1">{job.salary}</p>
                        </div>

                        {/* View Job Button */}
                        <button className="bg-green-500 text-white px-4 py-2 rounded-lg ml-4">
                            View Job ⬇
                        </button>
                    </div>
                ))}
            </section>
        </div>
    );
};

export default JobListings;
