import React, { useState } from "react";
import { NavLink } from "react-router-dom";
import bgImage from "./background.png"; // Background Image
import companyLogo from "./job-logo.png"; // Replace with actual logo path

const companies = Array(15).fill({
    id: 1,
    name: "K&D Garment",
    logo: companyLogo,
});

const Company = () => {
    const [searchTerm, setSearchTerm] = useState("");

    return (
        <div className="bg-gray-100 min-h-screen">
            {/* Navbar */}
            <nav className="bg-black text-white p-4 flex justify-between items-center">
                <div className="flex items-center space-x-2">
                    <img src="/logo.png" alt="Uni Earn" className="w-10 h-10" />
                    <h1 className="text-lg font-bold">Uni Earn</h1>
                </div>
                <div className="flex space-x-8 text-xl">
                    <NavLink to="/" className="hover:underline cursor-pointer">Home</NavLink>
                    <NavLink to="/company" className="border-b-2 border-white pb-1 cursor-pointer">Company</NavLink>
                    <NavLink to="/profile" className="hover:underline cursor-pointer">Profile</NavLink>
                    <NavLink to="/activities" className="hover:underline cursor-pointer">Activities</NavLink>
                    <NavLink to="/contact" className="hover:underline cursor-pointer">Contact</NavLink>
                    <NavLink to="/logout" className="hover:underline cursor-pointer text-red-400">Logout</NavLink>
                </div>
            </nav>

            {/* Hero Section */}
            <header
                className="relative flex flex-col justify-center items-center text-white text-align h-[50vh] bg-cover bg-center"
                style={{ backgroundImage: `url(${bgImage})` }}
            >
                <div className="absolute inset-0 bg-black bg-opacity-50"></div>
                <div className="relative z-10 text-center">
                    <h1 className="text-4xl md:text-6xl font-bold">
                        Find Your Perfect <br />
                        <span className="text-blue-400">Part-Time Job</span>
                    </h1>

                    {/* Search Bar */}
                    <div className="flex bg-white rounded-full p-2 mt-6 w-full max-w-md">
                        <input
                            type="text"
                            placeholder="Search Location..."
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            className="flex-grow p-2 outline-none text-black rounded-l-full"
                        />
                        <button className="bg-blue-500 px-6 py-2 rounded-r-full text-white font-semibold">Search</button>
                    </div>
                </div>
            </header>

            {/* Companies Section */}
            <section className="max-w-6xl mx-auto p-6">
                <h2 className="text-2xl font-bold mb-4">Companies</h2>

                <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-5 gap-6">
                    {companies.map((company, index) => (
                        <div key={index} className="bg-white shadow-md rounded-lg p-4 flex flex-col items-center">
                            <img src={company.logo} alt={company.name} className="w-16 h-16 rounded-full" />
                            <h3 className="text-md font-semibold text-gray-700 mt-2">{company.name}</h3>
                            <button className="mt-2 border border-green-500 text-green-500 px-4 py-2 rounded-lg font-medium hover:bg-green-500 hover:text-white transition">
                                View Deal
                            </button>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default Company;
