import React from "react";
import NavBar from "./NavBar"; // Import Navbar Component
import { Menu } from "lucide-react";

const HomePage = () => {
    return (
        <div className="min-h-screen bg-gray-100">
            {/* Navbar */}
            <NavBar />

            {/* Preview Button for Navbar */}
            <button className="fixed bottom-6 right-6 bg-blue-500 text-white p-4 rounded-full shadow-lg hover:bg-blue-600 transition">
                <Menu size={28} />
            </button>

            {/* Hero Section */}
            <header className="text-center p-10 bg-gradient-to-r from-blue-500 to-indigo-600 text-white">
                <h1 className="text-4xl md:text-6xl font-bold">Welcome to Uni Earn</h1>
                <p className="mt-2 text-lg">Find and apply for your perfect job!</p>
            </header>

            {/* Main Content */}
            <div className="container mx-auto px-4 py-10">
                <h2 className="text-3xl font-bold mb-6 text-center">Explore Our Features</h2>

                {/* Features Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                    <div className="bg-white p-6 shadow-lg rounded-lg">
                        <h3 className="text-xl font-bold mb-2">üî• Find Jobs</h3>
                        <p className="text-gray-600">Browse the best part-time and freelance jobs available.</p>
                    </div>

                    <div className="bg-white p-6 shadow-lg rounded-lg">
                        <h3 className="text-xl font-bold mb-2">üë®‚Äçüéì Student-Friendly</h3>
                        <p className="text-gray-600">Work part-time while managing your university schedule.</p>
                    </div>

                    <div className="bg-white p-6 shadow-lg rounded-lg">
                        <h3 className="text-xl font-bold mb-2">üíº Top Companies</h3>
                        <p className="text-gray-600">Apply to trusted companies that hire students.</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
