import React, { useState } from "react";

const FormattedOTP = () => {
    const [otp, setOtp] = useState("");

    const handleChange = (e) => {
        let value = e.target.value.replace(/\D/g, ""); // Remove non-numeric characters
        if (value.length > 6) value = value.slice(0, 6); // Limit to 6 digits

        // Format as 3:3 (e.g., 123:456)
        const formattedValue = value.length > 3 ? `${value.slice(0, 3)}:${value.slice(3)}` : value;
        setOtp(formattedValue);
    };

    const handleSubmit = () => {
        const cleanedOtp = otp.replace(":", ""); // Remove formatting for submission
        console.log("Formatted OTP:", otp);
        console.log("Clean OTP:", cleanedOtp);
    };

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
            {/* OTP Container */}
            <div className="bg-purple-900 bg-opacity-90 rounded-xl p-6 max-w-lg w-full text-center">
                <h2 className="text-white text-2xl font-bold mb-2">Enter OTP</h2>
                <p className="text-gray-300 mb-6">
                    Please enter the 6-digit code sent to your phone
                </p>

                {/* OTP Input */}
                <input
                    type="text"
                    value={otp}
                    onChange={handleChange}
                    maxLength="7" // Allow for the "3:3" format (6 digits + 1 colon)
                    className="w-full px-4 py-3 rounded-lg bg-purple-700 text-white text-center text-2xl placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 mb-6"
                    placeholder="123:456"
                />

                {/* Submit Button */}
                <button
                    onClick={handleSubmit}
                    className="bg-gradient-to-r from-purple-600 to-blue-600 text-white py-3 px-6 rounded-lg font-bold w-full hover:opacity-90"
                >
                    Submit
                </button>
            </div>
        </div>
    );
};

export default FormattedOTP;
