import userProfile from "./profile.png"; // Profile Picture

function App() {
    const [isEditing, setIsEditing] = useState(false);
    const [messageCount, setMessageCount] = useState(2); // Simulated unread messages

    // User Profile State
    const [profile, setProfile] = useState({
        bio: "",
        fullName: "Aathif Mohamed",
        mail:"aathifmhd2000@gmail.com",
        university: "University of Ruhuna",
        gender: "Male",
        mobileNo: "0779981298",
        emergencyMobileNo: "0779981298",
        address: "No 53/B Akurana, Kandy",
        password: "********",
        confirmPassword: "********",
    });

    // Handle input change
    const handleChange = (e) => {
        setProfile({
            ...profile,
            [e.target.name]: e.target.value,
        });
    };

    // Toggle edit mode
    const handleEditToggle = () => {
        setIsEditing(!isEditing);
    };

    return (
        <div className="min-h-screen bg-white">
            {/* Hero Section */}
            <div
                className="relative h-[400px] bg-cover bg-center"
                style={{ backgroundImage: 'url("https://images.unsplash.com/photo-1559136555-9303baea8ebd?auto=format&fit=crop&q=80")' }}
            >
                <div className="absolute inset-0 bg-black bg-opacity-50">
                    <div className="max-w-7xl mx-auto h-full flex flex-col justify-center px-6">
                        <h1 className="text-7xl font-bold text-white">
                            Welcome,
                            <br />
                            <span className="text-[#6B7AFF]">{profile.fullName}</span>
                        </h1>
                    </div>
                </div>
            </div>

            {/* Profile Section */}
            <div className="max-w-7xl mx-auto px-6 py-12">
                <div className="bg-white rounded-3xl shadow-lg p-8 mb-12">
                    {/* Profile Header */}
                    <div className="flex items-center space-x-6 mb-8">

                        <img src={userProfile} alt="Profile"
                            className="w-32 h-32 rounded-full object-cover"
                        />
                        <div className="flex-1">
                            <h2 className="text-2xl font-bold">{profile.fullName}</h2>
                            <p className="text-gray-600">{profile.mail}</p>
                        </div>
                        <button
                            onClick={() => setIsEditing(!isEditing)}
                            className="px-6 py-2 bg-blue-500 text-white rounded-lg flex items-center space-x-2 hover:bg-blue-600 transition-colors"
                        >
                            <Edit2 size={18} />
                            <span>Edit</span>
                        </button>
                    </div>

                    {/* Profile Form */}
                    <div className="grid grid-cols-2 gap-6">
                        <div className="col-span-2">
                            <label className="block text-sm font-medium text-gray-700 mb-2">Bio</label>
                            <textarea
                                className="w-full p-3 border rounded-lg bg-gray-50"
                                rows={4}
                                disabled={!isEditing}
                                placeholder="Write something about yourself..."
                            />
                        </div>

                        <ProfileField label="Full Name" value={profile.fullName} disabled={!isEditing} />
                        <ProfileField label="University" value={profile.university} disabled={!isEditing} />
                        <ProfileField label="Gender" value={profile.gender} disabled={!isEditing} icon={<ChevronDown size={18} />} />
                        <ProfileField label="Mobile No" value={profile.mobileNo} disabled={!isEditing} />
                        <ProfileField label="Address" value={profile.address} disabled={!isEditing} icon={<ChevronDown size={18} />} />
                        <ProfileField label="Emergency Mobile No" value={profile.emergencyMobileNo} disabled={!isEditing} />
                        <ProfileField label="Password" value={profile.password} type="password" disabled={!isEditing} />
                        <ProfileField label="Confirm Password" value={profile.confirmPassword} type="password" disabled={!isEditing} />
                    </div>
                </div>

                {/* Company Feedback Section */}
                <div>
                    <h2 className="text-2xl font-bold mb-6">Company Feedback</h2>
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
                        <FeedbackCard
                            image={user}
                            name="Floyd Miles"
                            rating={4}
                            review="Amet minim mollit non deserunt ullamco est sit aliqua dolor do amet sint. Velit officia consequat duis enim velit mollit. Exercitation veniam consequat sunt nostrud amet."
                        />
                        <FeedbackCard
                            image="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d"
                            name="Ronald Richards"
                            rating={4}
                            review="ullamco est sit aliqua dolor do amet sint. Velit officia consequat duis enim velit mollit. Exercitation veniam consequat sunt nostrud amet."
                        />
                        <FeedbackCard
                            image="https://images.unsplash.com/photo-1639149888905-fb39731f2e6c"
                            name="Savannah Nguyen"
                            rating={3}
                            review="Amet minim mollit non deserunt ullamco est sit aliqua dolor do amet sint. Velit officia consequat duis enim velit mollit."
                        />
                    </div>
                    <div className="flex justify-center space-x-2">
                        <div className="w-2 h-2 rounded-full bg-blue-500"></div>
                        <div className="w-2 h-2 rounded-full bg-gray-300"></div>
                        <div className="w-2 h-2 rounded-full bg-gray-300"></div>
                    </div>
                </div>
            </div>

            {/* Floating Message Button - Bottom Right */}
            <button className="fixed bottom-6 right-6 bg-green-500 text-white p-4 rounded-full shadow-lg flex items-center justify-center hover:bg-green-600 transition">
                <ChatBubbleOvalLeftEllipsisIcon className="w-8 h-8" />

                {/* Notification Badge */}
                {messageCount > 0 && (
                    <span className="absolute -top-2 -right-2 bg-red-500 text-white text-xs w-6 h-6 rounded-full flex items-center justify-center">
                        {messageCount}
                    </span>
                )}
            </button>
        </div>
    );
}

function NavLink({ icon, text, active = false, className = '' }) {
    return (
        <a
            href="#"
            className={`flex items-center space-x-1 ${
                active ? 'text-white' : 'text-gray-400'
            } hover:text-white transition-colors ${className}`}
        >
            {icon}
            <span>{text}</span>
        </a>
    );
}

function ProfileField({ label, value, type = 'text', disabled = true, icon = null }) {
    return (
        <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">{label}</label>
            <div className="relative">
                <input
                    type={type}
                    value={value}
                    disabled={disabled}
                    className="w-full p-3 border rounded-lg bg-gray-50"
                />
                {icon && (
                    <div className="absolute right-3 top-1/2 -translate-y-1/2 text-gray-400">
                        {icon}
                    </div>
                )}
            </div>
        </div>
    );
}

function FeedbackCard({ image, name, rating, review }) {
    return (
        <div className="bg-white rounded-xl shadow-md p-6">
            <div className="flex items-center space-x-4 mb-4">
                <img src={image} alt={name} className="w-12 h-12 rounded-full object-cover" />
                <div>
                    <h3 className="font-semibold">{name}</h3>
                    <div className="flex space-x-1">
                        {[...Array(5)].map((_, i) => (
                            <Star
                                key={i}
                                size={16}
                                className={i < rating ? 'text-yellow-400 fill-current' : 'text-gray-300'}
                            />
                        ))}
                    </div>
                </div>
            </div>
            <p className="text-gray-600 text-sm">{review}</p>
        </div>
    );
}

export default App;

