import React, { useState } from "react";
import "./Signup.css";
import { useNavigate } from "react-router-dom";

const Signup = () => {
    const navigate = useNavigate();
    const [pinEntered, setPinEntered] = useState(false);

    const onNavigateToSignIn = () => {
        navigate("/sign-in");
    };

    // Handle Next Button Click
    const onNavigateToPinPage = () => {
        alert("Enter the PIN");
        setPinEntered(true); // Set the flag to true to show PIN input
    };

    // Handle Create Account Click
    const onCreateAccount = () => {
        if (!pinEntered) {
            alert("Please enter the PIN first!");
            return;
        }
        alert("Account created successfully!");
        navigate("/home"); // Navigate to home after successful signup
    };

    return (
        <div className="relative w-full h-screen bg-cover bg-center signin-container">
            {/* Header */}
            <header className="absolute top-10 left-10 z-10 text-white">
                <h1 className="text-4xl font-bold">SIGN UP TO YOUR</h1>
                <p className="text-4xl font-bold text-blue-500">ADVENTURE!</p>
            </header>

            {/* Signup Form */}
            <div className="relative z-20 flex flex-col items-center justify-center h-full">
                <div className="bg-opacity-90 rounded-lg p-8 w-11/12 max-w-4xl">
                    <h2 className="text-white text-3xl font-bold mb-6 text-center">SIGN UP</h2>
                    <form className="grid grid-cols-1 md:grid-cols-2 gap-4 bg-container">
                        {/* First Name */}
                        <div>
                            <input
                                type="text"
                                placeholder="First Name"
                                className="w-full px-4 py-3 rounded-lg placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-blue-500 text-color"
                            />
                        </div>
                        {/* Last Name */}
                        <div>
                            <input
                                type="text"
                                placeholder="Last Name"
                                className="w-full px-4 py-3 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        {/* University */}
                        <div>
                            <select className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500">
                                <option value="">Choose Your University</option>
                                <option value="University of Colombo">University of Colombo</option>
                                <option value="University of Ruhuna">University of Ruhuna</option>
                                <option value="University of Moratuwa">University of Moratuwa</option>
                                {/* Add more universities */}
                            </select>
                        </div>
                        {/* University Mail */}
                        <div>
                            <input
                                type="email"
                                placeholder="University Mail"
                                className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        {/* Mobile No */}
                        <div>
                            <input
                                type="text"
                                placeholder="Mobile No"
                                className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        {/* Emergency Mobile No */}
                        <div>
                            <input
                                type="text"
                                placeholder="Emergency Mobile No"
                                className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        {/* Address */}
                        <div>
                            <input
                                type="text"
                                placeholder="Address"
                                className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        {/* Gender */}
                        <div>
                            <select className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500">
                                <option value="">Gender</option>
                                <option value="male">Male</option>
                                <option value="female">Female</option>
                            </select>
                        </div>
                        {/* Password */}
                        <div>
                            <input
                                type="password"
                                placeholder="Password"
                                className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                        {/* Confirm Password */}
                        <div>
                            <input
                                type="password"
                                placeholder="Confirm Password"
                                className="w-full px-4 py-3 rounded-lg text-color focus:outline-none focus:ring-2 focus:ring-blue-500"
                            />
                        </div>
                    </form>

                    {/* Submit Button */}
                    <div className="mt-6 text-center">
                        {!pinEntered ? (
                            <button
                                className="bg-gradient-to-r signin-button text-white py-3 px-6 rounded-lg font-bold hover:opacity-90"
                                onClick={onNavigateToPinPage}
                            >
                                Next
                            </button>
                        ) : (
                            <div>
                                {/* PIN Input */}
                                <h2 className="text-white text-xl font-bold mt-4">Enter PIN</h2>
                                <div className="flex justify-center gap-2 my-4">
                                    {[...Array(6)].map((_, index) => (
                                        <input
                                            key={index}
                                            type="text"
                                            maxLength="1"
                                            className="w-12 h-12 border border-gray-300 text-center text-lg rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                                        />
                                    ))}
                                </div>

                                {/* Create Account Button */}
                                <button
                                    className="bg-gradient-to-r from-purple-500 to-blue-500 text-white py-3 px-6 rounded-lg font-bold hover:opacity-90 w-full"
                                    onClick={onCreateAccount}
                                >
                                    Create Account
                                </button>
                                <p className="text-red-500 text-center mt-2 cursor-pointer">Resend Code</p>
                            </div>
                        )}

                        <p className="signin-link">
                            Already have an account?{" "}
                            <span onClick={onNavigateToSignIn} className="text-blue-400 font-bold cursor-pointer">
                                Sign In
                            </span>
                        </p>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Signup;
