import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Signup = () => {
    const navigate = useNavigate();
    const [showPinPopup, setShowPinPopup] = useState(false);
    const [pin, setPin] = useState(["", "", "", "", "", ""]);

    // Handle PIN input change
    const handlePinChange = (index, value) => {
        if (value.length > 1) return; // Only allow single digit input
        const newPin = [...pin];
        newPin[index] = value;
        setPin(newPin);
    };

    // Handle Next Button (Open PIN Popup)
    const handleNextClick = () => {
        setShowPinPopup(true);
    };

    // Handle Create Account Button
    const handleCreateAccount = () => {
        alert("Account Created Successfully!");
        navigate("/home"); // Redirect to Home Page after success
    };

    return (
        <div className="relative w-full h-screen bg-cover bg-center flex flex-col items-center justify-center">
            {/* Signup Form */}
            <div className="bg-white shadow-lg rounded-lg p-8 w-11/12 max-w-3xl">
                <h2 className="text-black text-3xl font-bold mb-6 text-center">SIGN UP</h2>

                <form className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {/* First Name */}
                    <input type="text" placeholder="First Name" className="w-full px-4 py-3 border rounded-lg" />

                    {/* Last Name */}
                    <input type="text" placeholder="Last Name" className="w-full px-4 py-3 border rounded-lg" />

                    {/* University */}
                    <select className="w-full px-4 py-3 border rounded-lg">
                        <option value="">Choose Your University</option>
                        <option value="University of Colombo">University of Colombo</option>
                        <option value="University of Peradeniya">University of Peradeniya</option>
                        <option value="University of Ruhuna">University of Ruhuna</option>
                    </select>

                    {/* University Email */}
                    <input type="email" placeholder="University Mail" className="w-full px-4 py-3 border rounded-lg" />

                    {/* Mobile No */}
                    <input type="text" placeholder="Mobile No" className="w-full px-4 py-3 border rounded-lg" />

                    {/* Emergency Mobile No */}
                    <input type="text" placeholder="Emergency Mobile No" className="w-full px-4 py-3 border rounded-lg" />

                    {/* Address */}
                    <input type="text" placeholder="Address" className="w-full px-4 py-3 border rounded-lg" />

                    {/* Gender */}
                    <select className="w-full px-4 py-3 border rounded-lg">
                        <option value="">Gender</option>
                        <option value="Male">Male</option>
                        <option value="Female">Female</option>
                    </select>

                    {/* Password */}
                    <input type="password" placeholder="Password" className="w-full px-4 py-3 border rounded-lg" />

                    {/* Confirm Password */}
                    <input type="password" placeholder="Confirm Password" className="w-full px-4 py-3 border rounded-lg" />
                </form>

                {/* Next Button */}
                <div className="mt-6 text-center">
                    <button
                        className="bg-gradient-to-r from-purple-500 to-blue-500 text-white py-3 px-6 rounded-lg font-bold hover:opacity-90"
                        onClick={handleNextClick}
                    >
                        Next
                    </button>
                </div>
            </div>

            {/* PIN Code Popup */}
            {showPinPopup && (
                <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center">
                    <div className="bg-white rounded-lg p-6 shadow-lg w-96">
                        <h2 className="text-lg font-bold text-black mb-4">PIN</h2>
                        <p className="text-gray-600 mb-4">Enter 6-digit verification code sent to your phone number</p>

                        {/* PIN Input Boxes */}
                        <div className="flex justify-center space-x-2">
                            {pin.map((digit, index) => (
                                <input
                                    key={index}
                                    type="text"
                                    value={digit}
                                    maxLength="1"
                                    className="w-12 h-12 text-xl text-center border rounded-lg"
                                    onChange={(e) => handlePinChange(index, e.target.value)}
                                />
                            ))}
                        </div>

                        {/* Create Account Button */}
                        <div className="mt-6 text-center">
                            <button
                                className="bg-gradient-to-r from-purple-500 to-blue-500 text-white py-3 px-6 rounded-lg font-bold hover:opacity-90 w-full"
                                onClick={handleCreateAccount}
                            >
                                Create Account
                            </button>
                        </div>

                        {/* Resend Code */}
                        <p className="text-red-500 text-sm text-center mt-3 cursor-pointer hover:underline">Resend Code</p>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Signup;
